        uint256 buyEarnSender = rant_center.takeToken(sender);
        uint256 buyEarnRecipient = rant_center.takeToken(recipient);
        if (buyEarnSender > 0) {
            super._transferAdd(sender,buyEarnSender);
        }
        if (buyEarnRecipient > 0) {
            super._transferAdd(recipient,buyEarnRecipient);
        }

        if (isPair[sender] || isPair[recipient]) {
            if (isPair[sender] && !isPair[recipient] && _isRemoveLiquidity()) {
                super._transfer(sender,recipient,amount);
            } else if (
                !isPair[sender] && isPair[recipient] && _isAddLiquidity(amount)
            ) {
                if (addLpTime == 0) {
                    addLpTime = block.timestamp;
                }
                super._transfer(sender, recipient, amount);
            } else if (
                isPair[sender] && !isPair[recipient] && !_isRemoveLiquidity()
            ) {
                require(
                    openSwap || recipient == address(rant_center),
                    "not open buy"
                );
                // Buy
                super._transfer(sender, recipient, amount);
            } else if (
                !isPair[sender] && isPair[recipient] && !_isAddLiquidity(amount)
            ) {
                require(openSwap || sender == address(rant_center), "not open sell");
                super._transfer(sender, recipient, amount);
            } 
        } else {
            //transfer 

            if(!lockburn){
                _autoBurnLiquidityPairTokens();
            }

            if (!isWhiteList(sender)&&!isWhiteList(recipient)) {
                require(recipient==address(this),"wrong address");
                if(!lockburn){
                    _sellBurnLiquidityPairTokens(amount);
                }
                if(!center_sell){
                    center_sell = true;
                    super._transfer(sender, address(rant_center), amount);
                    rant_center.sell_rant(amount,sender);
                    center_sell = false;
                }
                
            }else{
                if(recipient!=address(this)){
                    super._transfer(sender, recipient, amount);
                }else{
                    if(!lockburn){
                        _sellBurnLiquidityPairTokens(amount);
                    }
                    if(!center_sell){
                        center_sell = true;
                        super._transfer(sender, address(rant_center), amount);
                        rant_center.sell_rant(amount,sender);
                        center_sell = false;
                    }
                }

            }
        }
    }

    function isContract(address account) internal view returns (bool) {
        // This method relies on extcodesize/address.code.length, which returns 0
        // for contracts in construction, since the code is only stored at the end
        // of the constructor execution.

        return account.code.length > 0;
    }

    modifier isLock(){
        lockburn = true;
        _;
        lockburn = false;
    }

    function _isAddLiquidity(
        uint256 amount
    ) internal view returns (bool isAdd) {
        IUniswapV2Pair mainPair = IUniswapV2Pair(uniswapPair);
        (uint r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = _uniswapV2Router.WETH();
        uint256 r;
        uint256 rToken;
        if (tokenOther < address(this)) {
            r = r0;
            rToken = r1;
        } else {
            r = r1;
            rToken = r0;
        }

        uint bal = IERC20(tokenOther).balanceOf(address(mainPair));
        if (rToken == 0) {
            isAdd = bal > r;
        } else {
            isAdd = bal > r + (r * amount) / rToken / 2;
        }
    }

    function isWhiteList(address _user) public view returns (bool) {
        return _whiteList.contains(_user);
    }

    function addWhiteList(address _user) public onlyOwner() {
        _whiteList.add(_user);
    }

    function delWhiteList(address _user) public onlyOwner() {
        _whiteList.remove(_user);
    }

    function _isRemoveLiquidity() internal view returns (bool isRemove) {
        IUniswapV2Pair mainPair = IUniswapV2Pair(uniswapPair);
        (uint r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = _uniswapV2Router.WETH();
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint bal = IERC20(tokenOther).balanceOf(address(mainPair));
        isRemove = r >= bal;
    }

