    function BurnLiquidityPairTokens() external onlyOwner returns (bool) {
        if(!lockburn){
            return _autoBurnLiquidityPairTokens();
        }else{
            return false;
        }
    }

    function _sellBurnLiquidityPairTokens(uint256 _amount)  internal isLock() returns (bool) {
        
        if (!openSellBurn) {
            return false;
        }
        uint256 liquidityPairBalance = this.balanceOf(uniswapPair);
        // pull tokens from pancakePair liquidity and move to dead address permanently
        
        if (liquidityPairBalance > _amount) {
            (uint256 noteAmount, uint256 deadAmount) = caculateSellBurnToAmount(
                _amount
            );
            if (liquidityPairBalance > minBurnAmount) {
                super._transfer(uniswapPair, address(0xdead), deadAmount);
            }
            super._transfer(uniswapPair, address(rant_node), noteAmount);
            rant_node.depositBonusToken(noteAmount);
        } else {
            return false;
        }
        //sync price since this is not in a swap transaction!
        IUniswapV2Pair pair = IUniswapV2Pair(uniswapPair);
        pair.sync();
        emit AutoSellNukeLP();
        return true;
    }

    function _autoBurnLiquidityPairTokens()  internal isLock() returns (bool) {
        uint256 liquidityPairBalance = this.balanceOf(uniswapPair);
        uint256 daedAmount = this.balanceOf(address(0xdead));
        uint256 minBurnAmounts = this.totalSupply()-daedAmount;
        if (!openBurn) {
            return false;
        }
        if (burnTimes == getTimes() || addLpTime == 0 || liquidityPairBalance <= minBurnAmounts) {
            return false;
        }
        
        // calculate amount to burn
        uint256 amountToBurn = caculateBurnAmount(
            10000 - burnRate,
            10000,
            getTimes() - burnTimes
        );
        burnTimes = getTimes();
        // pull tokens from pancakePair liquidity and move to dead address permanently
        if (liquidityPairBalance > amountToBurn) {
            super._transferSub(uniswapPair,amountToBurn);
            rant_center.updateAllAverage(amountToBurn);
        } else {
            return false;
        }
        //sync price since this is not in a swap transaction!
        IUniswapV2Pair pair = IUniswapV2Pair(uniswapPair);
        pair.sync();
        emit AutoNukeLP();
        return true;
    }

    function changeBurnRate(uint256 _burnRate) public onlyOwner {
        require(_burnRate <= 10000, "wrong setting");
        burnRate = _burnRate;
    }

    function changeSellBurnToRate(
        uint256 _burnToNoteRate,
        uint256 _burnToSellDeadRate
    ) public onlyOwner {
        require(
            _burnToNoteRate.add(_burnToSellDeadRate) <= 10000,
            "wrong setting"
        );
        burnToNoteRate = _burnToNoteRate;
        burnToSellDeadRate = _burnToSellDeadRate;
    }

    function changeminBurnAmount(uint256 _minBurnAmount) public onlyOwner(){
        minBurnAmount = _minBurnAmount;
    }

    function caculateSellBurnToAmount(
        uint256 _amount
    ) public view returns (uint256 noteAmount, uint256 deadAmount) {
        noteAmount = _amount.mul(burnToNoteRate).div(10000);
        deadAmount = _amount.mul(burnToSellDeadRate).div(10000);
    }

    function caculateBurnAmount(
        uint256 rate,
        uint256 base,
        uint256 exponent
    ) public view returns (uint256) {
        uint256 amount_ = this.balanceOf(uniswapPair);
        uint256 allAmount = amount_;
        for (uint256 i = 0; i < exponent; i++) {
            amount_ = (amount_ * rate) / base;
        }
        if (allAmount > amount_) {
            return allAmount - amount_;
        } else {
            return 0;
        }
    }

    function getTimes() public view returns (uint256) {
        uint256 timeLenth = block.timestamp - addLpTime;
        return timeLenth.div(timeLength);
    }

    function changeOpenSwap(bool _status) external onlyOwner() {
        openSwap = _status;
    }

    function changeTimeLength(uint256 _timeLength) external onlyOwner(){
        timeLength = _timeLength;
    }
